msg.userData = msg.payload;

msg.url = msg.payload.url.trim();
return msg;

const { JSDOM } = jsdom;
const html = msg.payload;
const dom = new JSDOM(`${html}`);

dom.window.document.querySelectorAll('style').forEach(link => {
    link.remove();
});
dom.window.document.querySelectorAll('script').forEach(link => {
    link.remove();
});

const document = dom.window.document;
const pageText = document.body.textContent;

msg.text = pageText;

let table = dom.window.document.querySelector('table');
let ckeckTable = table?.textContent ? true : false;

if (ckeckTable) {
    const headers = Array.from(table.querySelectorAll('tr:first-child th, tr:first-child td')).map(td => {
        const text = td.textContent.trim();
        return text !== "" ? text : undefined;
    }).filter(Boolean);

    const rows = Array.from(table.querySelectorAll('tr:not(:first-child)'));
    const tableData = rows.map(row => {
        const rowData = Array.from(row.querySelectorAll('td')).map(td => td.textContent.trim());
        return Object.fromEntries(headers.map((header, index) => [header, rowData[index]]));
    });

} else {
     msg.pageContent = pageText;
}

const MAX_LENGTH = 500;

msg.result = [];
let currentParagraph = '';

const sentences = msg.pageContent.replace(/\s+/g, ' ').split(/([.?!])+/).filter(sentence => sentence.trim() !== '');

for (const sentence of sentences) {
    if (currentParagraph.length + sentence.length <= MAX_LENGTH) {
        currentParagraph += sentence;
    } else {
        msg.result.push(currentParagraph.trim());
        currentParagraph = sentence;
    }
}

if (currentParagraph.trim() !== '') {
    msg.result.push(currentParagraph.trim());
}

msg.payload = msg.result;

return msg;

const data = msg.pageContent;
msg.OPENAI_API_KEY = msg.userData.openAIKey;
msg.model = msg.userData.model;

msg.messages= [
    {
        role: "system",
        content: "act as a world class marketer",
    },
    {
        role: "user", content: `You are an AI assistant who's a world class marketer. You have deep expertise analysing company websites and are highly skilled at reverse engineering the target audience of a company based on their website's text.Your task is to extract the target audience.

// Output only the target audience with no pre-text or post-text. Make sure your answer is detailed and specific.

Here is the text from a company's website:${data} ` },
];



return msg;

